<?phpif($action==""){$col_back="&nbsp;";	}$script_js.="<script src=\"".$config['backendurl']."/template/js/plugins/fileupload/bootstrap-fileupload.min.js\"></script>\r\n";$sql = "SELECT type,basename, extension, namatampilan, maxdimension, maxfilesize,usecaption,caption FROM decoration  ORDER BY id";$random=uniqid();$result = $mysql->query($sql);echo "<form name=\"formheader\" method=\"POST\" class=\"form-vertical\" action=\"".backendurl("$modul/save")."\" enctype=\"multipart/form-data\">\r\n";echo "<input type=\"hidden\" name=\"action\" value=\"setheader\" />\r\n";echo "<input type=\"hidden\" name=\"headertype\" value=\"0\" />\r\n";while (list($type,$basename, $extension, $namatampilan, $maxdimension, $maxfilesize,$usecaption,$caption) = $mysql->row($result)) {	list($max,$mwidth,$mheight)=explode(";",$maxdimension);	$dimension="size {$mwidth}px X {$mheight}px";	if($mwidth >220 or $mheight>70)	{	$mwidth=$mwidth*0.4;	$mheight=$mheight*0.4;	}	echo <<<END<div class="control-group"><label for="textfield" class="control-label">$namatampilan ($dimension):</label><div class="controls">		<div class="fileupload fileupload-new" data-provides="fileupload">		<div class="fileupload-new thumbnail" style="width: {$mwidth}px; height: {$mheight}px"><img src="$large_url/$basename.$extension?random=$random" /></div>		<div class="fileupload-preview fileupload-exists thumbnail" style="max-width: {$mwidth}px; max-height: {$mheight}px; line-height: 20px;"></div>		<div>			<span class="btn btn-file"><span class="fileupload-new">Select image</span><span class="fileupload-exists">Change</span>			<input type="file" name='$basename' MAX_FILE_SIZE="$maxfilesize" /></span>			<a href="#" class="btn fileupload-exists" data-dismiss="fileupload">Remove</a>		</div>	</div>	END;if($usecaption){echo "<div class=\"caption\">";$caption=json_decode($caption,true);foreach($list_lang as $i =>$v){ echo "<input type=\"text\" name=\"{$basename}_title_$v\" placeholder=\"Judul ($v)\" value=\"".$caption["title_$v"]."\"><br/>";}foreach($list_lang as $i =>$v){echo "<textarea name=\"{$basename}_content_$v\" placeholder=\"Konten ($v)\">".$caption["content_$v"]."</textarea><br/>";}}echo <<<END</div></div><hr>END;}echo "<div class=\"row-fluid\">";echo "<div class=\"control-group\">";echo "<label for=\"textfield\" class=\"control-label\">$namatampilan ($dimension):</label>";echo "<div class=\"controls\"><input type=\"submit\" value=\""._SIMPAN."\" /></div>\r\n";echo "</div></div></form>\r\n";?>