<?php/*Catatan pakai yona-validation:Form :	Form menggunakan class yona-validation	Tombol submit name=submitVar  : nama element harus sama dengan var   * */$hari_ini=date("Y-m-d H:i:s");$admin_id=$_SESSION['s_id'];		$validation->set_validation(array('var'=>'nama','label'=>'Cabang'))->minlength(1)->required();//$validation->set_validation(array('var'=>'otorisasi','label'=>'Otorisasi'))->minlength(4)->required();//$validation->generate_js_validation();if($action=="save" or $action=="update") {	$id=$_POST['id'];	$kode=$_POST['kode'];	if($action=="save"){		$is_duplicate=$mysql->get1value("SELECT count(id) FROM master_cabang WHERE nama='$nama' ");	}	if($action=="update") {		$is_duplicate=$mysql->get1value("SELECT count(id) FROM master_cabang WHERE nama='$nama' AND id<>$id ");	}		if($is_duplicate) {		$validation->set_validation(array('var'=>'nama','label'=>'Nama'))->custom_msg($is_duplicate,"Nama Cabang harus unik");	}		if($validation->valid()){		$r_post=array(		'nama',		'alamat'		);				$sql_r = array();				if($action=="save") {			$sql_r[]="created_by='$admin_id'";			$sql_r[]="created_date='$hari_ini'";		}				if($action=="update") {			$sql_r[]="modified_by='$admin_id'";			$sql_r[]="modified_date='$hari_ini'";		}						foreach($r_post as $i => $v) {			$post=cleanInput($_POST[$v]);			$sql_r[]="$v = '$post'";		}				if($action=="save") {			$sql=" INSERT INTO master_cabang SET ".join(" ,",$sql_r);			$q=$mysql->query($sql);			$last_id=$mysql->insert_id();		}				if($action=="update") {			$sql=" UPDATE master_cabang SET ".join(" ,",$sql_r)." WHERE id=$id ";			$q=$mysql->query($sql);			/*			$ispublish=$_POST['ispublish']==1?1:0;			$sql1=" UPDATE master_profil SET ispublish=$ispublish WHERE id_cabang=$id ";			$q1=$mysql->query($sql1);			*/		}						} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." cabang gagal, data tidak valid",backendurl("$modul".($action=="update"?"/edit/$id":"/add")));	}		if($q){		sweetalert2($type="success",$msg=($action=="update"?"Update":"Tambah")." cabang berhasil",backendurl("$modul"));	} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." cabang gagal. ",backendurl("$modul".($action=="update"?"/edit/$id":"/add")));	}	}if($action=="del"){$id=cleanInput($id,'numeric');/*$r=$mysql->query("SELECT thumbnail,ebook from buku WHERE id='$id'");list($file_thumbnail,$file_ebook)=$mysql->row($r);//del gambar if(file_exists("$path_thumbnail/$file_thumbnail")){	unlink("$path_thumbnail/$file_thumbnail");}//del pdf if(file_exists("$path_thumbnail/$file_ebook")){	unlink("$path_thumbnail/$file_ebook");}*/ //update log	$valid=true;	$mysql->autocommit(false);	$q1=$mysql->query("UPDATE master_cabang SET deleted_by=$admin_id,deleted_date='$hari_ini' WHERE id=$id ");	if(!$q1){ $valid=false;}			$q2=$mysql->query("INSERT INTO master_cabang_log SELECT * FROM master_cabang WHERE id=$id ");	$sql="DELETE FROM master_cabang WHERE id='$id'";	$r=$mysql->query($sql);	if($r and $valid){		$mysql->commit();		$mysql->autocommit(true);		sweetalert2($type="success",$msg="Hapus cabang berhasil",backendurl("$modul"));	} else {		$mysql->rollback();			sweetalert2($type="warning",$msg="Hapus cabang gagal",backendurl("$modul"));	}	}?>