<?php/*Catatan pakai yona-validation:Form :	Form menggunakan class yona-validation	Tombol submit name=submitVar  : nama element harus sama dengan var   * */$hari_ini=date("Y-m-d H:i:s");$admin_id=$_SESSION['s_id'];		$validation->set_validation(array('var'=>'value','label'=>'Isi'))->minlength(1)->required();$validation->generate_js_validation();if($action=="save" or $action=="update") {	$id=$_POST['id'];			 		if($validation->valid()){		$r_post=array(		'value'		);				$sql_r = array();						if($action=="save") {			$sql_r[]="created_by='$admin_id'";			$sql_r[]="created_date='$hari_ini'";		}				if($action=="update") {			$sql_r[]="modified_by='$admin_id'";			$sql_r[]="modified_date='$hari_ini'";		}				foreach($r_post as $i => $v) {			if($v=='konfigurasi_singkat' or $v=='konfigurasi_lengkap') {				$post=cleanInput($_POST[$v],'html');			} else {				$post=cleanInput($_POST[$v]);			}			$sql_r[]="$v = '$post'";		}				if($action=="save") {			$sql=" INSERT INTO master_config SET ".join(" ,",$sql_r);			$q=$mysql->query($sql);			$last_id=$mysql->insert_id();		}				if($action=="update") {			$sql=" UPDATE master_config SET ".join(" ,",$sql_r)." WHERE id=$id ";						$q=$mysql->query($sql);		}						} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." konfigurasi gagal, data tidak valid",backendurl("$modul".($action=="update"?"/edit/$id":"/add")));	}		if($q){		sweetalert2($type="success",$msg=($action=="update"?"Update":"Tambah")." konfigurasi berhasil",backendurl("$modul"));	} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." konfigurasi gagal. ",backendurl("$modul".($action=="update"?"/edit/$id":"/add")));	}	}if($action=="del"){$id=cleanInput($id,'numeric');/*$r=$mysql->query("SELECT thumbnail,ebook from buku WHERE id='$id'");list($file_thumbnail,$file_ebook)=$mysql->row($r);//del gambar if(file_exists("$path_thumbnail/$file_thumbnail")){	unlink("$path_thumbnail/$file_thumbnail");}//del pdf if(file_exists("$path_thumbnail/$file_ebook")){	unlink("$path_thumbnail/$file_ebook");}*/ //update log	$valid=true;	$mysql->autocommit(false);	$sql="DELETE FROM master_config WHERE id='$id'";	$r=$mysql->query($sql);	if($r and $valid){		$mysql->commit();		$mysql->autocommit(true);		sweetalert2($type="success",$msg="Hapus konfigurasi berhasil",backendurl("$modul"));	} else {		$mysql->rollback();			sweetalert2($type="warning",$msg="Hapus konfigurasi gagal",backendurl("$modul"));	}	}?>