<?phpif($action=="add" OR $action=="edit"){if($action=='edit') {	$q=$mysql->query("	SELECT 		id,		judul,		value	FROM		master_config WHERE id=$id	");	$d=$mysql->assoc($q);	foreach($d as $field => $value) {		$_POST[$field]=$_POST[$field]==''?$value:$_POST[$field];	}}$do_action=backendurl("$modul/".($action=="add"?"save":"update"));$label_action=$action=="add"?"Tambah":"Edit";$form_judul=$form->element_Textbox("Judul","judul");$form_value=$form->element_Textbox("Isi","value");$judul=$_POST['judul'];echo <<<END  <div class="card card-primary">              <div class="card-header">                <h3 class="card-title">$label_action $judul</h3>              </div>              <!-- /.card-header -->              <!-- form start -->              <form role="form" method="POST" id="form-member"  class="form-member yona-validation" action="$do_action"  novalidate enctype="multipart/form-data">				<input type="hidden" name="id" value="$id" />                <div class="card-body"                  <div class="form-group">                    $form_value                                   </div>                  <div class="card-footer">                   <button type="button" class="btn btn-default" onclick="history.back(-1);">Batal</button>&nbsp;&nbsp;&nbsp;<button type="submit" name="submit" value="1" class="btn btn-primary">Submit</button>                </div>              </form>            </div>            <!-- /.card -->END;}if($action=="view" or $action==""){$btn_tambah=button_add("$modul/add");echo <<<END<div class="card">		<div class="card-header">		  <h3 class="card-title">Master Konfigurasi</h3>		  		  <div class="float-right">$btn_tambah</div>		</div>		<!-- /.card-header -->		<div class="card-body">				<table id="datalist" class="table table-bordered table-striped responsive no-wrap">				<thead>				<tr>				<th>Judul</th>				<th>Isi</th>				<th style="width:80px;">Action</th>				</tr>				</thead>				</table>		</div>		<!-- /.card-body -->	  </div></div>END;}if($action=="data") {		$column_order = array('judul','value');	$column_search = array('judul','value');	$order = array('judul' => 'ASC');		if(isset($_POST['order'])) { // here order processing		$order_by = " ORDER BY {$column_order[$_POST['order']['0']['column']]} {$_POST['order']['0']['dir']}";	} else {		$order = $order;				$order_by = " ORDER BY judul ASC ";	}	if ($_POST['length'] != -1 AND $_POST['length']!="") {		$where_limit = "LIMIT {$_POST['start']}, {$_POST['length']}";	}	$i = 0;	$sql_search=array();	foreach ($column_search as $item) { // loop column 				if($_POST['search']['value']) { // if datatable send POST for search						$sql_search[]= " $item LIKE '%{$_POST['search']['value']}%' ";		}		$i++;	}		if(count($sql_search)>0){	$sql_r[]=" ".join(" OR ",$sql_search)." ";	}		$sql=" 	SELECT 		id,		judul,		value	FROM 		master_config		";		$sql.=" WHERE 1=1 ";	if(count($sql_r)>0){		$sql.=" AND  (".join(" OR ",$sql_r).") ";	}		$result_total = $mysql->query($sql);	$total=$mysql->num_rows($result_total);	$sql .= " $order_by $where_limit";		$result = $mysql->query($sql);		$data = array();			$gotopage = $_POST['start']/$_POST['length'];	$no = $_POST['start'];	while($d = $mysql->fetch_assoc($result)) {				$no++;		$row = array();				$row[]=$d['judul'];		$row[]=$d['value'];		$action_edit=$daftar_hak[$modul]['edit']==1?btn_edit(backendurl("$modul/edit/".$d['id'])):'';		$action_delete=$daftar_hak[$modul]['del']==1?btn_delete(backendurl("$modul/del/".$d['id'])):'';		$row[]='<div class="btn-group btn-group-sm">'.$action_edit.$action_delete.'</div>';				$data[] = $row;	}		$output = array(		"draw" => $_POST['draw'],		"recordsTotal" => $total,		"recordsFiltered" => $total,		"data" => $data	);	die(json_encode($output));}?>