<?php/*Catatan pakai yona-validation:Form :	Form menggunakan class yona-validation	Tombol submit name=submitVar  : nama element harus sama dengan var   * */$hari_ini=date("Y-m-d H:i:s");$admin_id=$_SESSION['s_id'];		$validation->set_validation(array('var'=>'title','label'=>'Judul'))->minlength(2)->required();$validation->set_validation(array('var'=>'grup','label'=>'Grup'))->required();$validation->generate_js_validation();if($action=="save" or $action=="update") {	$id=$_POST['id'];		if($validation->valid()){		$r_post=array(		'title',		'grup',		'publish'		);				$sql_r = array();				if($action=="save") {			$sql_r[]="created_by='$admin_id'";			$sql_r[]="created_date='$hari_ini'";		}				if($action=="update") {			$sql_r[]="modified_by='$admin_id'";			$sql_r[]="modified_date='$hari_ini'";		}						foreach($r_post as $i => $v) {			if($v!='content') {				$post=cleanInput($_POST[$v]);			} else {				$post=$_POST[$v];			}			$sql_r[]="$v = '".addslashes($post)."'";		}				if($action=="save") {			$sql=" INSERT INTO app_category SET ".join(" ,",$sql_r);			$q=$mysql->query($sql);			$last_id=$mysql->insert_id();		}				if($action=="update") {			$sql=" UPDATE app_category SET ".join(" ,",$sql_r)." WHERE id=$id ";			$q=$mysql->query($sql);		}						} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." Category gagal, data tidak valid",backendurl("$modul".($action=="update"?"/edit/$id":"/add")));	}		if($q){		sweetalert2($type="success",$msg=($action=="update"?"Update":"Tambah")." Category berhasil",backendurl("$modul"));	} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." Category gagal. ",backendurl("$modul".($action=="update"?"/edit/$id":"/add")));	}	}if($action=="del"){$id=cleanInput($id,'numeric');	$valid=true;	$mysql->autocommit(false);		$sql="DELETE FROM app_category WHERE id='$id'";	$r=$mysql->query($sql);	if($r and $valid){		$mysql->commit();		$mysql->autocommit(true);		sweetalert2($type="success",$msg="Hapus app_category berhasil",backendurl("$modul"));	} else {		$mysql->rollback();			sweetalert2($type="warning",$msg="Hapus app_category gagal",backendurl("$modul"));	}	}