<?php/*Catatan pakai yona-validation:Form :	Form menggunakan class yona-validation	Tombol submit name=submitVar  : nama element harus sama dengan var   * */$hari_ini=date("Y-m-d H:i:s");$admin_id=$_SESSION['s_id'];		$validation->set_validation(array('var'=>'course_sub_id','label'=>'Course ID'))->required();$validation->set_validation(array('var'=>'type','label'=>'Jenis'))->required();$validation->set_validation(array('var'=>'title','label'=>'Judul'))->minlength(3)->required();/*$validation->set_validation(array('var'=>'content','label'=>'Isi Konten'))->required();*/ $validation->generate_js_validation();if($action=="save" or $action=="update") {	$id=$_POST['id'];	$course_sub_id=$_POST['course_sub_id'];		$course_id = $mysql->get1value("SELECT course_id FROM app_course_sub WHERE id=$course_sub_id");		if($validation->valid()){		$r_post=array(		'title',		'content',		'course_sub_id',		'video_url',		'video_embed_url',		'quiz_id',        'type',		);				$sql_r = array();				if($action=="save") {			$sql_r[]="created_by='$admin_id'";			$sql_r[]="created_date='$hari_ini'";		}				if($action=="update") {			$sql_r[]="modified_by='$admin_id'";			$sql_r[]="modified_date='$hari_ini'";		}						foreach($r_post as $i => $v) {			if($v!='content' and $v!='video_embed_url') {				$post=cleanInput($_POST[$v]);			} else {				$post=$_POST[$v];			}			$sql_r[]="$v = '".addslashes($post)."'";		}				if($action=="save") {			$sql=" INSERT INTO app_course_material SET ".join(" ,",$sql_r);			$q=$mysql->query($sql);			$last_id=$mysql->insert_id();		}				if($action=="update") {			$sql=" UPDATE app_course_material SET ".join(" ,",$sql_r)." WHERE id=$id ";			$q=$mysql->query($sql);		}						} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." Materi gagal, data tidak valid",backendurl("app_course_material".($action=="update"?"/edit/$id":"/add"))."?course_sub_id=$course_sub_id");	}		if($q){		sweetalert2($type="success",$msg=($action=="update"?"Update":"Tambah")." Materi berhasil",backendurl("app_course_sub")."?course=$course_id");	} else {		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." Materi gagal. ",backendurl("app_course_material".($action=="update"?"/edit/$id":"/add"))."?course_sub_id=$course_sub_id");	}	}if($action=="del"){	$id=cleanInput($id,'numeric');	$course_sub_id=$_GET['course_sub_id'];	$course_id = $mysql->get1value("SELECT course_id FROM app_course_sub WHERE id=$course_sub_id");	$valid=true;	$mysql->autocommit(false);		$sql="DELETE FROM app_course_material WHERE id='$id'";	$r=$mysql->query($sql);	if($r and $valid){		$mysql->commit();		$mysql->autocommit(true);		sweetalert2($type="success",$msg="Hapus materi berhasil",backendurl("app_course_sub")."?course=$course_id");	} else {		$mysql->rollback();			sweetalert2($type="warning",$msg="Hapus materi gagal",backendurl("app_course_sub")."?course=$course_id");	}	}