<?phpfunction info_jumlah_siswa_aktif() {global $mysql;$data = array();	$q = $mysql->query("SELECT count(id) jumlah,class FROM quiz_member WHERE status=1  GROUP BY class ORDER BY class");if($q and $mysql->num_rows($q)>0) {	while($d = $mysql->fetch_assoc($q)) {		$data[]=array("class"=>$d['class'],"jumlah"=>$d['jumlah']);	}}return $data;}function info_jumlah_siswa_tidak_aktif() {global $mysql;$data = array();	$q = $mysql->query("SELECT count(id) jumlah,class FROM quiz_member WHERE status=0  GROUP BY class ORDER BY class");if($q and $mysql->num_rows($q)>0) {	while($d = $mysql->fetch_assoc($q)) {		$data[]=array("class"=>$d['class'],"jumlah"=>$d['jumlah']);	}}return $data;}?>