<?phpif($action=="update"){	if(Form::isValid("update{$modul}",false) and $_POST['form']=="update{$modul}") 	{		$r=$mysql->query("SELECT * from modul");	while($d=$mysql->assoc($r))	{	foreach($list_lang as $i =>$v)	{	${$d['modul']."_title_$v"}=cleanInput($_POST[$d['modul']."_title_$v"]);	}		$sql_r=array();	$sql="	UPDATE modul SET ";	foreach($list_lang as $i =>$v)	{	$sql_r[]="title_$v='".${$d['modul']."_title_$v"}."'";	}		$sql.=join(",",$sql_r);	$sql.="	WHERE modul='".$d['modul']."'";		$exe=$mysql->query($sql);	}		if($exe)	{	Form::clearValues("update{$modul}"); 	redirecto(_BERHASILUPDATE,"success","view");	}		}	else	{	redirecto(_GAGALUPDATE,"error","");	}}?>