<?phpif($action=="" or $action=="view"){$form = new Form("update{$modul}");$form->configure(array(	"prevent" => array("bootstrap", "jQuery", "focus"),	"action" =>backendurl("$modul/update") ));$form->addElement(new Element_HTML('<legend>'._FORM_EDIT.'</legend>'));$form->addElement(new Element_Hidden("form", "update{$modul}"));//$form->addElement(new Element_Hidden("id", "$id"));$r=$mysql->query("SELECT * from modul order by urutan");while($d=$mysql->assoc($r)){	$form->addElement(new Element_HTML("<b>".strtoupper($d['modul'])."</b>"));	foreach($list_lang as $i =>$v)	{	$reg=array("value"=>$d['title_'.$v]);	$form->addElement(new Element_Textbox(_FIELDJUDUL." ($v)",$d['modul']."_title_$v",$reg));	}	$form->addElement(new Element_HTML('<hr>'));}//$form->addElement(new Element_Textbox(_URL,"url", array("required" => 1,"value"=>$d['url'])));$form->addElement(new Element_Button(_SIMPAN));$form->addElement(new Element_Button(_BATAL, "button", array(	"onclick" => "history.go(-1);")));$form->render();}?>