<?phpif($action=="add" OR $action=="edit"){if($action=='edit') {	$q=$mysql->query("	SELECT 		id,		date_format(tanggal,'%d/%m/%Y') tanggal,		date_format(tanggal_expired,'%d/%m/%Y') tanggal_expired,		title,		content	FROM		pengumuman WHERE id=$id	");	$d=$mysql->assoc($q);		foreach($d as $field => $value) {		$_POST[$field]=$_POST[$field]==''?$value:$_POST[$field];	}}$do_action=backendurl("$modul/".($action=="add"?"save":"update"));$label_action=$action=="add"?"Tambah":"Edit";$form_tanggal=$form->element_Textbox("Tanggal Tampil","tanggal");$form_tanggal_expired=$form->element_Textbox("Tanggal Expired","tanggal_expired");$form_title=$form->element_Textbox("Judul","title");$form_content=$form->element_Textarea("Isi Pengumuman","content",array('class'=>'usetiny'));echo <<<END  <div class="card card-navy">              <div class="card-header">                <h3 class="card-title">$label_action Pengumuman</h3>              </div>              <!-- /.card-header -->              <!-- form start -->              <form role="form" method="POST" id="form-member"  class="form-member yona-validation" action="$do_action"  novalidate enctype="multipart/form-data">				<input type="hidden" name="id" value="$id" />                <div class="card-body">                  <div class="form-group">					<div class="row">						<div class="col-md-6">							$form_tanggal						</div>						<div class="col-md-6">							$form_tanggal_expired						</div>					</div>                  </div>                  <div class="form-group">                    $form_title                                  </div>                  <div class="form-group">                    $form_content                                      </div>                <div class="card-footer">                   <button type="button" class="btn btn-default" onclick="history.back(-1);">Batal</button>&nbsp;&nbsp;&nbsp;<button type="submit" name="submit" value="1" class="btn btn-primary">Submit</button>                </div>              </form>            </div>            <!-- /.card -->END;$script_js=<<<END<script>$(document).ready(function(){				new GijgoDatePicker(document.getElementById("tanggal"), { keyboardNavigation: true, modal: true, header: true, footer: true, format: 'dd/mm/yyyy' });		new GijgoDatePicker(document.getElementById("tanggal_expired"), { keyboardNavigation: true, modal: true, header: true, footer: true, format: 'dd/mm/yyyy' });							});</script>END;}if($action=="view" or $action==""){$btn_tambah=button_add("$modul/add");echo <<<END<div class="card card-navy">		<div class="card-header">		  <h3 class="card-title">Pengumuman</h3>		  <div class="float-right">$btn_tambah</div>		</div>		<!-- /.card-header -->		<div class="card-body">				<table id="datalist" class="table table-bordered table-striped responsive no-wrap">				<thead>				<tr>				<th style="width:40px;">No</th>				<th>Tanggal</th>				<th>Expired</th>				<th>Judul</th>				<th style="width:80px;">Action</th>				</tr>				</thead>				</table>		</div>		<!-- /.card-body -->	  </div></div>END;}if($action=="data") {		$column_order = array('id','tanggal','title');	$column_search = array('title','content');	$order = array('id' => 'DESC');		if(isset($_POST['order'])) { // here order processing		$order_by = " ORDER BY {$column_order[$_POST['order']['0']['column']]} {$_POST['order']['0']['dir']}";	} else {		$order = $order;				$order_by = " ORDER BY id DESC ";	}	if ($_POST['length'] != -1 AND $_POST['length']!="") {		$where_limit = "LIMIT {$_POST['start']}, {$_POST['length']}";	}	$i = 0;	$sql_search=array();	foreach ($column_search as $item) { // loop column 				if($_POST['search']['value']) { // if datatable send POST for search						$sql_search[]= " $item LIKE '%{$_POST['search']['value']}%' ";		}		$i++;	}		if(count($sql_search)>0){	$sql_r[]=" ".join(" OR ",$sql_search)." ";	}		$sql=" 	SELECT 		id,		tanggal,		tanggal_expired,		title,		content	FROM 		pengumuman		";		$sql.=" WHERE 1=1 ";	if(count($sql_r)>0){		$sql.=" AND  (".join(" OR ",$sql_r).") ";	}		$result_total = $mysql->query($sql);	$total=$mysql->num_rows($result_total);	$sql .= " $order_by $where_limit";		$result = $mysql->query($sql);		$data = array();		$gotopage = $_POST['start']/$_POST['length'];	$no = $_POST['start'];	while($d = $mysql->fetch_assoc($result)) {		$no++;		$row = array();				$row[]=$no;		$row[]=$d['tanggal'];		$row[]=$d['tanggal_expired'];		$row[]=$d['title'];		$action_edit=btn_edit(backendurl("$modul/edit/".$d['id']));		$action_delete=btn_delete(backendurl("$modul/del/".$d['id']));		/*		$action_edit='<a href="'.backendurl("$modul/edit/".$d['id']).'"><i class="fas fa-edit" aria-hidden="true"></i></a>&nbsp;&nbsp;&nbsp;&nbsp;';		$action_delete='<a class=""  title="Hapus buku"				data-toggle="modal" 				data-target="#modal-default" 				data-title="Hapus data" 				data-body="Apakah anda yakin ingin menghapus data ini?"				data-btn2=""				data-btn2name=""				data-btn1="'.backendurl("$modul/del/".$d['id']).'"				data-btn1name="Hapus"				>				<i class="fas fa-trash"></i>		</a>';		*/		$row[]='<div class="btn-group btn-group-sm">'.$action_view.$action_edit.$action_delete.'</div>';				$data[] = $row;	}			$output = array(		"draw" => $_POST['draw'],		"recordsTotal" => $total,		"recordsFiltered" => $total,		"data" => $data	);	die(json_encode($output));}?>