<?phpif($action=="update"){	$username=cleanInput($_POST['username']);	$ulogin=md5($username);	$password="";	if($_POST['password']!='')	{	$password=md5(sha1($_POST['password']));	}	$fullname=cleanInput($_POST['fullname']);									$r_update=array();			if($username!="")			{			$r_update[]="username='$username'";			$r_update[]="ulogin='$ulogin'";			}			if($fullname!="")$r_update[]="fullname='$fullname'";			if($password!="")$r_update[]="password='$password'";											$sql="UPDATE user SET ";			$sql.=join(",",$r_update);						$sql.=" WHERE username='".$_SESSION['s_username']."'";				$r=$mysql->query($sql);			if($r)			{				if($username!="")				{				$_SESSION['s_username']=$username;				}				sweetalert2($type="success","Update profil berhasil",backendurl("$modul"));			}			else			{				sweetalert2($type="warning","Update profil gagal",backendurl("$modul"));			}		}?>