<?php/*Catatan pakai yona-validation:Form :	Form menggunakan class yona-validation	Tombol submit name=submitVar  : nama element harus sama dengan var   * */$hari_ini=date("Y-m-d H:i:s");$admin_id=$_SESSION['s_id'];		//$validation->set_validation(array('var'=>'grade[]',''=>'Grade'))->required();//$validation->generate_js_validation();if($action=="update") {	$valid=true;	$mysql->autocommit(false);	$id=$_POST['id'];		if(count($_POST['class'])>0) {		foreach($_POST['class'] as $id_member => $class) {			$q=$mysql->query("UPDATE quiz_member SET class='$class' WHERE id=$id_member ");			if(!$q) {				$valid=false;			}			if($mysql->affected_rows($q)>0) {				$q=$mysql->query("INSERT INTO quiz_class_log SET member_id='$id_member',class='$class',created_by='$admin_id',created_date='$hari_ini' ");			}		}	}			if($valid){		$mysql->commit();		$mysql->autocommit(true);		sweetalert2($type="success",$msg=($action=="update"?"Update":"Tambah")." Kelas berhasil",backendurl("$modul"));	} else {		$mysql->rollback();		sweetalert2($type="warning",$msg=($action=="update"?"Update":"Tambah")." Kelas gagal. ",backendurl("$modul".($action=="update"?"/edit/$id":"/add")));	}	}