<?phpfunction delete_directory($dirname) {         if (is_dir($dirname))           $dir_handle = opendir($dirname);     if (!$dir_handle)          return false;     while($file = readdir($dir_handle)) {           if ($file != "." && $file != "..") {                if (!is_dir($dirname."/".$file))                     unlink($dirname."/".$file);                else                     delete_directory($dirname.'/'.$file);           }     }     closedir($dir_handle);     rmdir($dirname);     return true;}function change_filename($full_file_name,$new_name) {	$file_parts = pathinfo($full_file_name);	return $new_name.'.'.$file_parts['extension'];}function create_directory($path,$name) {	if(!file_exists("$path/$name")) {		$a=mkdir("$path/$name");		chmod("$path/$name", 0777);	}}function convert_to_html($filename) {	global $backup_folder;	global $temp_folder;	global $html_folder;	global $quiz_folder;	global $this_path;	$file_parts = pathinfo($filename);	/*	if(in_array($file_parts['extension'],array('odt','doc','docx'))) {				return false;	}	*/ 	//set tmp	unlink("$this_path/$html_folder/$quiz_folder");	create_directory($this_path,$temp_folder);	create_directory($this_path,$html_folder);	create_directory("$this_path/$html_folder",$quiz_folder);		$cmd = "export HOME='$this_path/$temp_folder' && /usr/bin/libreoffice --headless --convert-to html '$this_path/$backup_folder/$filename' --outdir '$this_path/$html_folder/$quiz_folder'";		$exec=shell_exec($cmd);	if($exec!='NULL') {		return true;	}	return false;}function upload_wordx_trimmer($string){		$string	= strip_tags($string);	$string = str_replace("&#xD;",'',$string);	$string	= preg_replace('/\s+/', '', $string);	$string	= trim($string);		return $string;}function upload_wordx_cleaner($string){	$string = str_replace("&#xD;",'',$string);	$string = preg_replace('/\s\s+/', ' ', $string);	$string = trim($string);		return $string;}?>