<?phpfunction upload_wordx_trimmer($string){		$string	= strip_tags($string);	$string = str_replace("&#xD;",'',$string);	$string	= preg_replace('/\s+/', '', $string);	$string	= trim($string);		return $string;}function upload_wordx_cleaner($string){	$string = str_replace("&#xD;",'',$string);	$string = preg_replace('/\s\s+/', ' ', $string);	$string = trim($string);		return $string;}?>