body">				<table id="datalist" class="table table-bordered table-striped responsive no-wrap">				<thead>				<tr>				<th style="width:40px;">No</th>				<th>Kelas</th>				<th>Jumlah</th>				<th>Wali</th>				<th style="width:80px;">Action</th>				</tr>				</thead>				</table>		</div>		<!-- /.card-body -->	  </div></div>END;}if($action=="data") {				$sql=" 	SELECT x.class,x.jumlah,k.id_user,u.fullname FROM (		SELECT 			count(m.id) jumlah,m.class		FROM 			quiz_member m		GROUP BY m.class		ORDER BY m.class			) x	LEFT JOIN quiz_wali_kelas k ON k.class=x.class	LEFT JOIN user u ON u.id=k.id_user	";				$result = $mysql->query($sql);		$data = array();			while($d = $mysql->fetch_assoc($result)) {		$no++;		$row = array();				$row[]=$no;		$row[]=$d['class'];		$row[]=$d['jumlah'];		$row[]=$d['fullname'];		//$action_print=btn_print(backendurl("$modul/print/".$d['class']));				$action_edit='<a href="'.backendurl("$modul/edit/".$d['class']).'"><i class="fas fa-edit" aria-hidden="true"></i></a>&nbsp;&nbsp;&nbsp;&nbsp;';		/*		$action_delete='<a class=""  title="Hapus buku"				data-toggle="modal" 				data-target="#modal-default" 				data-title="Hapus data" 				data-body="Apakah anda yakin ingin menghapus data ini?"				data-btn2=""				data-btn2name=""				data-btn1="'.backendurl("$modul/del/".$d['id']).'"				data-btn1name="Hapus"				>				<i class="fas fa-trash"></i>		</a>';		*/		$row[]='<div class="btn-group btn-group-sm">'.$action_edit.'</div>';				$data[] = $row;	}			$output = array(		"draw" => $_POST['draw'],		"recordsTotal" => $total,		"recordsFiltered" => $total,		"data" => $data	);	die(json_encode($output));}?>