<?php$script_js.="<script src=\"".$config['backendurl']."/template/js/plugins/fileupload/bootstrap-fileupload.min.js\"></script>\r\n";$sql = "SELECT type,basename, extension, namatampilan, maxdimension, maxfilesize,usecaption,caption FROM slide_show ORDER BY id";$random=uniqid();$result = $mysql->query($sql);echo "<form id=\"slide_show\" name=\"formheader\" method=\"POST\" class=\"form-vertical\" action=\"".backendurl("$modul/save")."\" enctype=\"multipart/form-data\">\r\n";echo "<input type=\"hidden\" name=\"action\" value=\"setheader\" />\r\n";echo "<input type=\"hidden\" name=\"headertype\" value=\"0\" />\r\n";while (list($type,$basename, $extension, $namatampilan, $maxdimension, $maxfilesize,$usecaption,$caption) = $mysql->row($result)) {		list($max,$mwidth,$mheight)=explode(";",$maxdimension);	$dimension="size {$mwidth}px X {$mheight}px";	/*	if($mwidth >220 or $mheight>70)	{	$mwidth=$mwidth*0.4;	$mheight=$mheight*0.4;	}	*/	$mwidth="200";	$mheight="73";	echo <<<END<div class="control-group"><label for="textfield" class="control-label">$namatampilan ($dimension):</label><div class="controls">		<div class="fileupload fileupload-new" data-provides="fileupload">		<div class="fileupload-new thumbnail" style="width: {$mwidth}px; height: {$mheight}px"><img src="$large_url/{$basename}_200.$extension?random=$random" /></div>		<div class="fileupload-preview fileupload-exists thumbnail" style="max-width: {$mwidth}px; max-height: {$mheight}px; line-height: 10px;"></div>		<div>			<span class="btn btn-file"><span class="fileupload-new">Select image</span><span class="fileupload-exists">Change</span>			<input type="file" name='$basename' MAX_FILE_SIZE="$maxfilesize" /></span>			<a href="#" class="btn fileupload-exists" data-dismiss="fileupload">Remove</a>		</div>	</div>	END; echo <<<END</div></div>END;}echo "<div class=\"row-fluid\">";echo "<div class=\"control-group\">";echo "<div class=\"controls\"><input type=\"submit\" value=\""._SIMPAN."\" /><input type=\"submit\" value=\""._BARU."\" /></div>\r\n";echo "</div></div></form>\r\n";?>