ment_Textbox(_NAMA,"title_id"));$form->addElement(new Element_Textbox(_PEKERJAAN,"pekerjaan"));foreach($list_lang as $i =>$v){$form->addElement(new Element_Textarea(_KONTENT,"content_$v"));}$form->addElement(new Element_Button(_SIMPAN));$form->addElement(new Element_Button(_BATAL, "button", array(	"onclick" => "history.go(-1);")));$form->render();}if($action=="" or $action=="search" or $action=="view"){$form_search=form_search($keyword);}if($action=="view" or $action=="" or $action=="search"){$col_tambah=button_add("$modul/add");$sql="SELECT * FROM $modul ";if($action=="search"){$sql.=" WHERE ";$sql.=" title_id LIKE '%$keyword%' ";}echo "<script>$(document).ready(function(){$('#brand_row').sortable({update: function(event, ui){sort=$('#brand_row').sortable('serialize'); $.ajax({ 		type: 'POST',        url:'".backendurl("testimonial/sortitem")."',        data:sort,        success: function(html)        {	//	alert(html);        }        });}});});</script>";$r=$mysql->query($sql);$total_records = $mysql->numrows($r);$start = $screen * $max_page_list;$pages = ceil($total_records/$max_page_list);if ($pages>1) $col_pagination=pagination($screen);$sql.="  order by $sort_by $sort_order  LIMIT $start, $max_page_list";$r=$mysql->query($sql);echo "<table id='DataTables_Table_3_wrapper' class='view-table table table-hover table-nomargin table-bordered '>";echo "<thead><tr><th></th><th>No</th><th>"._THUMBNAIL."</th>";echo "<th>".orderBy("title_id",_JUDUL,$sort_order,$sort_by)."</th>";echo "<th>".orderBy("pekerjaan",_PEKERJAAN,$sort_order,$sort_by)."</th>";echo "<th>".orderBy("content_id",_KONTENT,$sort_order,$sort_by)."</th>";echo "<th>Action</th></tr></thead>";$no=($start+1);if($mysql->numrows($r)>0){echo '<tbody id="brand_row" class="ui-sortable">';while($d=$mysql->assoc($r)){echo "<tr id='list_{$d['id']}' class='rowmove'><td><span class='show_row_move'><i class='glyphicon-move'></i></span></td>